export const orderCreateReducer=(state={},action)=>{

    switch (action.type){
            case 'ORDER_CREATE_REQUEST':
                return{
                    loading:true
                }

            case 'ORDER_CREATE_SUCCESS':
                return {
                    loading:false,
                    success:true,
                    order:action.payload
                }

            case 'ORDER_CREATE_FAIL':
                return {
                    loading:false,
                    error:action.payload
                }    

             default:
                 return state   

    }



}



export const orderDetailsReducer=(state={orderItems:[],shippingAddress:{}},action)=>{

    switch (action.type){
            case 'ORDER_CREATE_REQUEST':
                return{
                    loading:true
                }

            case 'ORDER_CREATE_SUCCESS':
                return {
                    loading:false,
                    success:true,
                    order:action.payload
                }

            case 'ORDER_CREATE_FAIL':
                return {
                    loading:false,
                    error:action.payload
                }    

             default:
                 return state   

    }



}

export default orderCreateReducer
