import React,{useEffect} from 'react'
import axios from 'axios';
import { useDispatch,useSelector } from 'react-redux';
import { Row,Col } from 'react-bootstrap'
import Product from '../components/Product'
import Loader from '../components/Loader';

import { listProducts } from '../actions/productActions';

const HomeScreen = () => {

    const dispatch=useDispatch();

    const productList=useSelector(state=>state.productList) //getting from store combinereducer
    const {loading,error,products}=productList // getting loading error and products from productList


    //const [products,setProducts]=useState([]);

    useEffect(() => {
       
        
        dispatch(listProducts()); //getting products 




       /* const fetchproducts=async()=>{
            const res=await axios.get('/api/products')
            setProducts(res.data);
        
        }

        fetchproducts();

        */


    }, [dispatch])



    return (
        <>
            
            <h1>Latest Products</h1>
            {loading?<Loader></Loader> : error? <h2>{error}</h2>:
             <Row>
             {
                 products.map(product=>(

                     <Col sm={12} md={6} lg={4} xl={3}>
                          <Product  product={product}></Product>
                     </Col>
                 ))
             }
          </Row>
            
            }
            
           
        </>
    )
}

export default HomeScreen
